import React, { Component } from "react";
import { connect } from "react-redux";
import { Row, Container, Col, CardColumns } from "react-bootstrap";
import SingleCard from "./SingleCard";

const mapStateToProps = (state) => state;

class Favorites extends Component {
  render() {
    return (
      <Container>
        <Row className="homepage">
          <Col xs={12} lg={6} className="listCol">
            <CardColumns className="w-100">
              {this.props.favouriteJobList &&
                this.props.favouriteJobList.map((job, index) => (
                  <SingleCard job={job} key={index} />
                ))}
            </CardColumns>
          </Col>
        </Row>
      </Container>
    );
  }
}

export default connect(mapStateToProps)(Favorites);
