import React, { Component } from "react";
import { connect } from "react-redux";
import { Row, Container, Col, CardColumns, Table } from "react-bootstrap";
import SingleCard from "./SingleCard";
import { withRouter } from "react-router-dom";

const mapStateToProps = (state) => state;

const mapDispatchToProps = (dispatch) => ({
  selectedJob: (job) => dispatch({ type: "SINGLE_JOB", payload: job }),
});

class Favorites extends Component {
  render() {
    return (
      <Container>
        <Row className="homepage">
          <Col xs={12} lg={6} className="listCol">
            <CardColumns className="w-100">
              {this.props.listFavoriteJob &&
                this.props.listFavoriteJob.map((job, index) => (
                  //   console.log(job)
                  // <SingleCard job={job} key={index} />
                  <Table job={job} key={index}>
                    <thead>
                      <tr>
                        <th>Job Title</th>
                        <th>Last Name</th>
                        <th>Username</th>
                      </tr>
                    </thead>
                    <tbody>
                      <tr>
                        <td>{job.title}</td>
                        {/* <td>{this.props.job.location}</td> */}
                        <td>@mdo</td>
                      </tr>
                    </tbody>
                  </Table>
                ))}
            </CardColumns>
          </Col>
        </Row>
      </Container>
    );
  }
}

export default withRouter(
  connect(mapStateToProps, mapDispatchToProps)(Favorites)
);
