import React, { Component } from "react";
import { Row, Col } from "react-bootstrap";

export default class DetailPage extends Component {
  render() {
    const job = this.props.jobs.find(
      (job) => job.id === this.props.match.params.id
    );
    console.log(job);
    return job ? (
      <>
        <Row className="homepage2">
          <Col xs={12}>
            <h1 className="jobTitle">{this.props.jobTitle.title}</h1>
          </Col>
          <Col xs={12}>
            <h4>
              {this.props.jobTitle.location}, {this.props.jobTitle.type}
            </h4>
          </Col>
        </Row>

        <Row className="bg-colour-change2 ">
          <Col
            xs={12}
            className="d-flex justify-content-center align-items-center"
          >
            <h4>{this.props.jobTitle.company}</h4>
          </Col>
        </Row>
      </>
    ) : (
      <div>There is no job like you requested.</div>
    );
  }
}
